import React, { useState } from 'react';
import axios from 'axios';
import './IntroText.css';

function IntroText({ setResponse }) {
    const [formData, setFormData] = useState({
        articulo: '',
        resumen: '',
        longitud: '300',
      });

    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true); 
        try {
            const res = await axios.post('http://127.0.0.1:5000/api/data', formData);
            setResponse(res.data);
        } catch (error) {
            console.error('Error sending data:', error);
            setResponse('Error sending data');
        } finally {
            setLoading(false)
        }
    };

    return (
        <div className='comp1'>
        <form onSubmit={handleSubmit}>
            <label><b>Enter the article to be summarized</b></label>
            <textarea
                name="articulo"
                placeholder="Copy the article here"
                value={formData.articulo}
                onChange={handleChange}
                rows="20"
                cols="110"
            />
            <br/>
            <label><b>Approximate word length of summary: </b></label>
            <input
                type="number"
                name="longitud"
                value={formData.longitud}
                onChange={handleChange}
                min="100"
                max="1000"
                placeholder="longitud"
            />
            <br/>
            <button className="b1" type="submit">Simplify</button>
        </form>
        <div className={`cargando ${loading ? 'cargando-con-texto' : ''}`}>
            {loading && <p>Generating summary, please wait...</p>}
        </div>
        </div>
    );
    }

export default IntroText;
