import React, { useState } from 'react';
import axios from 'axios';
import './OutText.css';

function OutText({ setResponse, response }) {
    const [formData, setFormData] = useState({
        text: response,
        longitud: '300',
      });
    const resumen = response;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://127.0.0.1:5000/api/data', formData);
      setResponse(res.data.message);
    } catch (error) {
      console.error('Error sending data:', error);
      setResponse('Error sending data');
    }
  };

  return (
    <div className='comp2'>
      <form onSubmit={handleSubmit}>
        <div className='texto'>
            <div className='c1'>
                <label>Articulo original</label>
                <textarea
                className='ta'
                name="text"
                placeholder="Copy the article here"
                value={formData.text}
                onChange={handleChange}
                rows="20"
                cols="60"
                />
                <br/>
            </div>
            <div className='c2'>
                <label>Resumen simplicado</label>
                <p>{resumen}</p>
            </div>
        </div>
        <label>Longitud aproximada de palabras del resumen: </label>
        <input
          type="number"
          name="longitud"
          value={formData.longitud}
          onChange={handleChange}
          min="100"
          max="1000"
          placeholder="longitud"
        />
        <br/>
        <button className="b1" type="submit">Simplify</button>
      </form>
      <p>Adri71 - Raquel</p>
    </div>
  );
}

export default OutText;
