import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';
import IntroText from './IntroText';
import OutText from './OutText';

function App() {
  const [formData, setFormData] = useState({
    articulo: '',
    resumen: '',
    longitud: '300'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async () => {
    setLoading(true);
    try {
      const res = await axios.post('http://127.0.0.1:5000/api/data', formData);
      setFormData({ ...formData, resumen: res.data.resumen });
    } catch (error) {
      console.error('Error sending data:', error);
      setError('Error sending data');
    } finally {
      setLoading(false);
    }
  };


  return (
    <div className="App">
      <header className="App-header">
        <h1>Summary App</h1>
        <p>Text summary and simplification application</p>
      </header>
      <main>
        {formData.resumen ? (
          <OutText formData={formData} setFormData={setFormData} loading={loading} handleSubmit={handleSubmit} />
        ) : (
          <IntroText formData={formData} setFormData={setFormData} loading={loading} handleSubmit={handleSubmit} />
        )}
        {error && <p className="error">{error}</p>}
      </main>
    </div>
  );
}

export default App;
