import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './OutText.css';

function OutText({ setResponse, response }) {
    const [formData, setFormData] = useState({
        articulo: response.articulo,
        resumen: response.resumen,
        longitud: response.longitud
    });

    const [loading, setLoading] = useState(false);

    useEffect(() => {
        setFormData({
            articulo: response.articulo,
            resumen: response.resumen,
            longitud: response.longitud
        });
    }, [response]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });  
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true); 
        try {
            const res = await axios.post('http://127.0.0.1:5000/api/data', formData);
            setResponse(res.data);
        } catch (error) {
            console.error('Error sending data:', error);
            setResponse('Error sending data');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className='comp2'>
        <form onSubmit={handleSubmit}>
            <div className='texto'>
                <div className='c1'>
                    <label><b>Original article</b></label>
                    <textarea
                        className='ta'
                        name="articulo"
                        placeholder="Copy the article here"
                        value={formData.articulo}
                        onChange={handleChange}
                        rows="20"
                        cols="60"
                    />
                    <br/>
                </div>
                <div className='c2'>
                    <label><b>Simplified summary</b></label>
                    {loading ? <p>Generating the summary, please wait...</p> : <p>{formData.resumen}</p>}
                </div>
            </div>
            <label><b>Approximate word length of summary:</b> </label>
            <input
                type="number"
                name="longitud"
                value={formData.longitud}
                onChange={handleChange}
                min="100"
                max="1000"
                placeholder="longitud"
            />
            <br/>
            <button className="b1" type="submit" disabled={loading}>
                {loading ? 'Simplifying...' : 'Simplify'}
            </button>
        </form>
        </div>
    );
    }

export default OutText;
