import React, { useState } from 'react';
import axios from 'axios';
import './FormComponent.css';

function FormComponent({ setResponse, response }) {
  const [formData, setFormData] = useState({ text: '' , longitud: 300});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://127.0.0.1:5000/api/data', formData);
      setResponse(res.data.message);
    } catch (error) {
      console.error('Error sending data:', error);
      setResponse('Error sending data');
    }
  };

  return (
    <div className='comp1'>
      <form onSubmit={handleSubmit}>
      <div className='c1'>
        <textarea
          name="text"
          placeholder="Copy the article here"
          value={formData.text}
          onChange={handleChange}
          rows="20"
          cols="110"
        />
      </div>
        <label>Longitud aproximada de palabras del resumen: </label>
        <input
          type="number"
          name="longitud"
          value={formData.longitud}
          onChange={handleChange}
          min="100"
          max="1000"
          placeholder="longitud"
        />
        <br/>
        <button className="b1" type="submit">Simplify</button>
      </form>
      <div className='c2'>
        {response && (
          <textarea
            readOnly
            value={response}
            rows="10"
            cols="60"
          />
        )}
      </div>
      <p>Adri11</p>
    </div>
  );
}

export default FormComponent;
